import React, { useState } from 'react';
import { login, register } from '../api';
import './AuthModal.css';

function AuthModal({ onClose, onSuccess }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      if (isLogin) {
        // –õ–æ–≥–∏–Ω
        const response = await login(email, password);
        const { access_token, user_id } = response.data;

        localStorage.setItem('token', access_token);
        localStorage.setItem('user', JSON.stringify({
          email,
          id: user_id,
        }));

        onSuccess({ email, id: user_id });
      } else {
        // –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
        await register(email, password, fullName);

        // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π –≤—Ö–æ–¥ –ø–æ—Å–ª–µ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
        const loginResponse = await login(email, password);
        const { access_token, user_id } = loginResponse.data;

        localStorage.setItem('token', access_token);
        localStorage.setItem('user', JSON.stringify({
          email,
          id: user_id,
        }));

        onSuccess({ email, id: user_id });
      }
    } catch (err) {
      setError(err.response?.data?.detail || '–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setEmail('');
    setPassword('');
    setFullName('');
    setError('');
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    resetForm();
  };

  return (
    <div className="auth-modal-overlay" onClick={onClose}>
      <div className="auth-modal" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close-btn" onClick={onClose}>
          <span>&times;</span>
        </button>

        <div className="modal-header">
          <h2 className="modal-title">
            {isLogin ? 'üîê –í—Ö–æ–¥ –≤ –∞–∫–∫–∞—É–Ω—Ç' : '‚ú® –°–æ–∑–¥–∞—Ç—å –∞–∫–∫–∞—É–Ω—Ç'}
          </h2>
          <p className="modal-subtitle">
            {isLogin ? '–í–æ–π–¥–∏—Ç–µ –≤ —Å–≤–æ–π –∞–∫–∫–∞—É–Ω—Ç' : '–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å –¥–ª—è –ø–æ–∫—É–ø–æ–∫'}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              placeholder="your@email.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="form-input"
            />
          </div>

          {!isLogin && (
            <div className="form-group">
              <label htmlFor="fullName">–ü–æ–ª–Ω–æ–µ –∏–º—è</label>
              <input
                id="fullName"
                type="text"
                placeholder="–í–∞—à–µ –∏–º—è"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                required
                className="form-input"
              />
            </div>
          )}

          <div className="form-group">
            <label htmlFor="password">–ü–∞—Ä–æ–ª—å</label>
            <input
              id="password"
              type="password"
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="form-input"
            />
          </div>

          {error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              <span>{error}</span>
            </div>
          )}

          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner"></span>
                –ó–∞–≥—Ä—É–∑–∫–∞...
              </>
            ) : (
              isLogin ? 'üîì –í–æ–π—Ç–∏' : 'üéâ –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è'
            )}
          </button>
        </form>

        <div className="modal-footer">
          <p className="toggle-text">
            {isLogin ? '–ù–µ—Ç –∞–∫–∫–∞—É–Ω—Ç–∞?' : '–£–∂–µ –µ—Å—Ç—å –∞–∫–∫–∞—É–Ω—Ç?'}
          </p>
          <button
            type="button"
            onClick={toggleMode}
            className="toggle-mode-button"
          >
            {isLogin ? '–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è' : '–í–æ–π—Ç–∏'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
